proposal.idl:
[NoInterfaceObject]
interface WEBGL_multi_draw_instanced {
  void multiDrawArraysInstancedWEBGL(
      GLenum mode,
      (Int32Array or sequence<GLint>) firstsList, GLuint firstsOffset,
      (Int32Array or sequence<GLsizei>) countsList, GLuint countsOffset,
      (Int32Array or sequence<GLsizei>) instanceCountsList, GLuint instanceCountsOffset,
      GLsizei drawcount);
  void multiDrawElementsInstancedWEBGL(
      GLenum mode,
      (Int32Array or sequence<GLint>) countsList, GLuint countsOffset,
      GLenum type,
      (Int32Array or sequence<GLsizei>) offsetsList, GLuint offsetsOffset,
      (Int32Array or sequence<GLsizei>) instanceCountsList, GLuint instanceCountsOffset,
      GLsizei drawcount);
};

samplecode.pre:
var ext = gl.getExtension("WEBGL_multi_draw_instanced");

{
  // multiDrawArraysInstanced variant.
  let firsts = new Int32Array(...);
  let counts = new Int32Array(...);
  let instanceCounts = new Int32Array(...);
  ext.multiDrawArraysInstancedWEBGL(
      gl.TRIANGLES, firsts, 0, counts, 0, instanceCounts, 0, firsts.length);
}

{
  // multiDrawElementsInstanced variant.
  // Assumes that the indices which have been previously uploaded to the
  // ELEMENT_ARRAY_BUFFER are to be treated as UNSIGNED_SHORT.
  let counts = new Int32Array(...);
  let offsets = new Int32Array(...);
  let instanceCounts = new Int32Array(...);
  ext.multiDrawElementsInstancedWEBGL(
      gl.TRIANGLES, counts, 0, gl.UNSIGNED_SHORT, offsets, 0, instanceCounts, 0,
      counts.length);
}

#extension GL_ANGLE_multi_draw : require
void main() {
    gl_Position = vec4(gl_DrawID, 0, 0, 1);
}
